window.addEventListener("load", ()=>
{
    display_keyboard();
    document.getElementById("key_火").style.fontWeight = "bold";
    document.getElementById("key_十").style.fontWeight = "bold";
})

window.addEventListener("keypress",(e)=>
{
    const sk = key_dictionary[e.key];
    if(sk != undefined && now_scene == 1)
    {
        inputKey(sk);
    }
    audio.pause();
    audio.currentTime = 0.06;
    audio.play();
})

window.addEventListener("keydown",(e)=>
{
    const sk = key_dictionary[e.key];
    if(sk != undefined)
    {
        const key = document.getElementById("key_" + sk);
        key.style.backgroundColor = "#ffaaaa";
    }
})

window.addEventListener("keyup",(e)=>
{
    const sk = key_dictionary[e.key];
    if(sk != undefined)
    {
        const key = document.getElementById("key_" + sk);
        key.style.backgroundColor = "#ffffffff";
    }
})

document.onkeydown = (e)=>
{
    if(e.key == " ")
    {
        if(now_scene == 0)
        {
            gameStart();
        }
        return false;
    }
}

function display_keyboard()
{
    //ノードの定義＆作成
    const div_node = document.getElementById("keyboard");
    const table_node = document.createElement("table");
    const tbody_node = document.createElement("tbody");
    //ペアレント
    div_node.appendChild(table_node);
    table_node.appendChild(tbody_node);
    //テーブルの設定
    table_node.classList.add("keyboard");
    let tr_node, td_node;
    for(let i = 0; i < key_data.length; i++)
    {
        tr_node = document.createElement("tr");
        tbody_node.appendChild(tr_node);
        //左列
        td_node = document.createElement("td");
        tr_node.appendChild(td_node);
        td_node.colSpan = i + 2;
        for(let j = 0; j < key_data[i].length; j++)
        {
            td_node = document.createElement("td");
            tr_node.appendChild(td_node);
            td_node.innerHTML = key_data[i][j].sk;
            td_node.colSpan = 3;
            td_node.classList.add("key");
            td_node.id = "key_" + key_data[i][j].sk;
        }
        switch(i)
        {
            case 0:
                td_node = document.createElement("td");
                tr_node.appendChild(td_node);
                td_node.colSpan = 2;
                break
            case 1:
                td_node = document.createElement("td");
                tr_node.appendChild(td_node);
                td_node.colSpan = 4;
                break;
            case 2:
                td_node = document.createElement("td");
                tr_node.appendChild(td_node);
                td_node.colSpan = 9;
                break;
        }
    }
    //スペース列
    tr_node = document.createElement("tr");
    tbody_node.appendChild(tr_node);
    //左列
    td_node = document.createElement("td");
    tr_node.appendChild(td_node);
    td_node.colSpan = 12;
    td_node = document.createElement("td");
    tr_node.appendChild(td_node);
    td_node.innerHTML = "Space";
    td_node.colSpan = 12;
    td_node.classList.add("key");
    td_node.id = "key_Space";
    td_node = document.createElement("td");
    tr_node.appendChild(td_node);
    td_node.colSpan = 10;
}

//スタート
let now_scene = 0;
let now_subject = "";
let now_structure_number = 0;
let now_subject_number = 0;
let timer = 0;
let interval;
let now_point = 0;

function gameStart()
{
    //スコア
    now_point = 0;
    //オプションの非表示
    const option_node = document.getElementById("option");
    option_node.hidden = true;
    //ゲーム画面の表示
    const game_node = document.getElementById("game_div");
    game_node.hidden = false;
    //キーボードの非表示
    if(option_data.keyboard == false)
    {
        const keyboard = document.getElementById("keyboard");
        keyboard.hidden = true;
    }
    //タイマー
    timer = 60;
    update_period = 1000;
    if(now_scene == 0)
    {
        now_scene = 1;
        interval = setInterval(UpdateTimer, update_period);
        setText();
    }
}

let update_period = 1000;

function UpdateTimer()
{
    timer--;
    if(timer < 0)
    {
        //終了
        clearInterval(interval);
        now_scene = 2;
        //表示の変更
        const game_div = document.getElementById("game_div");
        const keyboard = document.getElementById("keyboard");
        const result = document.getElementById("result");
        const score = document.getElementById("score");
        game_div.hidden = true;
        keyboard.hidden = false;
        result.hidden = false;
        score.innerHTML = now_point + " 点";
    }
    else
    {
        const timer_node = document.getElementById("time");
        timer_node.innerHTML = timer + " 秒";
    }
}

function changeOption(option, value)
{
    option_data[option] = value;
}

function setText()
{
    //お題の追加
    if(theme_pool.length == 0)
    {
        if(option_data.difficulty >=0)
        {
            theme_pool = theme_pool.concat(theme_data.easy);
        }
        if(option_data.difficulty >=1)
        {
            theme_pool = theme_pool.concat(theme_data.country);
            theme_pool = theme_pool.concat(theme_data.premier);
            theme_pool = theme_pool.concat(theme_data.chemical);
        }
        if(option_data.difficulty >=2)
        {
            theme_pool = theme_pool.concat(theme_data.yoji);
            theme_pool = theme_pool.concat(theme_data.chimei);
        }
    }
    //プールからの選択
    let n = Math.floor(Math.random() * theme_pool.length);
    let theme = theme_pool[n];
    theme_pool.splice(n, 1);
    now_structure_number = 0;
    now_subject_number = 0;
    let txt_subject = "";
    let txt_structure = "";
    let souketsu_dummy;
    now_subject = "";
    const subject_div = document.getElementById("subject");
    const structure_div = document.getElementById("structure");
    for(let i = 0; i < theme.length; i++)
    {
        souketsu_dummy = souketsu_data[theme[i]];
        if(souketsu_dummy == undefined){ continue; }
        txt_subject += "<div style='color:gray'>" + theme[i] + "</div>";
        for(let j = 0; j < souketsu_dummy.length; j++)
        {
            if(option_data.structure == true)
            {
                txt_structure += "<div style='color:gray'>" + souketsu_dummy[j] + "</div>";
            }
            else
            {
                txt_structure += "<div style='color:gray'>" + "?" + "</div>";
            }
            now_subject += souketsu_dummy[j];
        }
        if(i != theme.length - 1)
        {
            txt_structure += "<div>　</div>";
            now_subject += " ";
        }
    }
    subject_div.innerHTML = txt_subject;
    structure_div.innerHTML = txt_structure;
}

function inputKey(key)
{
    const subject_node = document.getElementById("subject");
    const structure_node = document.getElementById("structure");
    //正解
    if(now_subject[now_structure_number] == key)
    {
        structure_node.children[now_structure_number].style = "color:#black; font-weight: bold";
        structure_node.children[now_structure_number].innerHTML = key;
        now_structure_number++;
        //終了条件
        if(now_structure_number >= now_subject.length)
        {
            timer = Math.min(60, Math.ceil(timer + now_structure_number / 4));
            setText();
        }
        //空白条件
        if(now_subject[now_structure_number] == " ")
        {
            subject_node.children[now_subject_number].style = "color:#black; font-weight: bold";
            now_structure_number++;
            now_subject_number++;
        }
        now_point++;
    }
    //不正解
    else
    {
        clearInterval(interval);
        update_period *= 0.95;
        interval = setInterval(UpdateTimer, update_period);
    }
}

